svg.script:
displayTips();

function displayTips() {
    let tasks = document.getElementsByClassName('task12455275919615065002');
    for (let i = 0; i < tasks.length ; i++) {
      let task = tasks[i];
      let tip_id = task.id;
      let tip = document.getElementById('tip_12455275919615065002_'+tip_id);
      tip.style.display='none';
      tasks[i].tip = tip;
      tasks[i].addEventListener('mouseover', mouseOverEffect);
      tasks[i].addEventListener('mouseout', mouseOutEffect);
    }
}

function mouseOverEffect() {
  this.classList.add("task-highlight");
  this.tip.style.display='block';
}

function mouseOutEffect() {
  this.classList.remove("task-highlight");
  this.tip.style.display='none';
}
